            <div className="row justify-content-center">
                <div className="col-12 col-md-8">
                    <div className="translation-card p-4 mb-3">
                        <textarea 
                            className="form-control mb-3"
                            value={inputText}
                            onChange={(e) => setInputText(e.target.value)}
                            placeholder="Enter text"
                        />
                        <select 
