import React, { useState } from 'react';
import translate from 'translate-google-api';

const Translation = () => {
    const [inputText, setInputText] = useState('');
    const [translatedText, setTranslatedText] = useState('');
    const [sourceLang, setSourceLang] = useState('auto');
    const [targetLang, setTargetLang] = useState('en');


    const languageOptions = {
        "auto": "Detect Language",
        "en": "English",
        "es": "Spanish",
        "fr": "French",
        "de": "German",
        "zh-CN": "Chinese (Simplified)",
        "zh-TW": "Chinese (Traditional)",
        "da": "Danish",
        "nl": "Dutch",
        "fi": "Finnish",
        "el": "Greek",
        "hi": "Hindi",
        "it": "Italian",
        "ja": "Japanese",
        "ko": "Korean",
        "no": "Norwegian",
        "pl": "Polish",
        "pt": "Portuguese",
        "ru": "Russian",
        "sv": "Swedish",
        "tr": "Turkish",
        "cs": "Czech",
        "ro": "Romanian",
        "hu": "Hungarian",
        "sk": "Slovak",
        "sl": "Slovenian",
        "lt": "Lithuanian",
        "lv": "Latvian",
        "et": "Estonian",
        "bg": "Bulgarian",
        "hr": "Croatian",
        "sr": "Serbian",
        "uk": "Ukrainian",
        "he": "Hebrew",
        "ur": "Urdu",
        "ar": "Arabic",
        "fa": "Persian",
        "th": "Thai",
        "vi": "Vietnamese",
        "id": "Indonesian",
        // ... continue adding other languages
    };


    const handleTranslate = async () => {
        try {
            const result = await translate(inputText, { from: sourceLang, to: targetLang });
            setTranslatedText(result[0]);
        } catch (error) {
            console.error('Translation error:', error);
            setTranslatedText('Error in translation');
        }
    }

    return (
        <div>
            <textarea 
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                placeholder="Enter text"
            />
            <select value={sourceLang} onChange={(e) => setSourceLang(e.target.value)}>
                {Object.entries(languageOptions).map(([code, name]) => (
                    <option key={code} value={code}>{name}</option>
                ))}
            </select>
            <select value={targetLang} onChange={(e) => setTargetLang(e.target.value)}>
                {Object.entries(languageOptions).map(([code, name]) => (
                    <option key={code} value={code}>{name}</option>
                ))}
            </select>
            <button onClick={handleTranslate}>Translate</button>
            <button onClick={handleTranslate}>Testing the translate()</button>
            <div>
                <p>Translated Text:</p>
                <p>{translatedText}</p>
            </div>
        </div>
    );
}

export default Translation;
