import React, { useState } from 'react';
import axios from 'axios';

const Translation = () => {
    const [inputText, setInputText] = useState('');
    const [responseText, setResponseText] = useState('');
    const [targetLang, setTargetLang] = useState('en');

    const languageOptions = {
        "en": "English",
        "es": "Spanish",
        "fr": "French",
        "de": "German",
        "zh-CN": "Chinese (Simplified)",
        "zh-TW": "Chinese (Traditional)",
        "da": "Danish",
        "nl": "Dutch",
        "fi": "Finnish",
        "el": "Greek",
        "hi": "Hindi",
        "it": "Italian",
        "ja": "Japanese",
        "ko": "Korean",
        "no": "Norwegian",
        "pl": "Polish",
        "pt": "Portuguese",
        "ru": "Russian",
        "sv": "Swedish",
        "tr": "Turkish",
        "cs": "Czech",
        "ro": "Romanian",
        "hu": "Hungarian",
        "sk": "Slovak",
        "sl": "Slovenian",
        "lt": "Lithuanian",
        "lv": "Latvian",
        "et": "Estonian",
        "bg": "Bulgarian",
        "hr": "Croatian",
        "sr": "Serbian",
        "uk": "Ukrainian",
        "he": "Hebrew",
        "ur": "Urdu",
        "ar": "Arabic",
        "fa": "Persian",
        "th": "Thai",
        "vi": "Vietnamese",
        "id": "Indonesian",
        // ... continue adding other languages
    };

    const handleTranslate = async () => {
        try {
            const promptText = `Translate this to ${targetLang}: ${inputText}`;



            const response = await axios.post(
                'https://api.openai.com/v1/engines/davinci-codex/completions',
                {
                    prompt: promptText,
                    max_tokens: 60
                },
                {
                    headers: {
                        'Authorization': `Bearer Q3A1YL3AAZWPHNITZOX0A1W8ZI7W75UB6LYEOKAGFKQA47B11G3ZGOCCHMEPJHDJ`,
                        'Content-Type': 'application/json'
                    }
                }
            );
            setResponseText(response.data.choices[0].text);
        } catch (error) {
            console.error('Error:', error);
            setResponseText('Error in processing request');
        }
    };

    return (
        <div>
            <textarea 
                value={inputText}
                onChange={(e) => setInputText(e.target.value)}
                placeholder="Enter text"
            />
            <select value={targetLang} onChange={(e) => setTargetLang(e.target.value)}>
                {Object.entries(languageOptions).map(([code, name]) => (
                    <option key={code} value={code}>{name}</option>
                ))}
            </select>
            <button onClick={handleTranslate}>Translate</button>
            <div>
                <p>ChatGPT Response:</p>
                <p>{responseText}</p>
            </div>
        </div>
    );
}

export default Translation;
